import React, { useState, useEffect } from "react";
import { Tezos } from "@taquito/taquito";
import { TezBridgeSigner } from "@taquito/tezbridge-signer";
import Menu from "./Menu";
import xtz from "./xtz_price";
import "./App.css";
import "./bulma.css";

/* PUT HERE THE CONTRACT ADDRESS FOR YOUR OWN SANDBOX! */

const KT_ledger = "KT1RigSYegqvkCkEoHMfgXY4XLgknZufrFEa"
const KT_token = "KT1GxBkvP5aiAtBu9MwAWBgxRpaCeLCcBfNj"

const shortenAddress = addr =>
  addr.slice(0, 6) + "..." + addr.slice(addr.length - 6);

const mutezToTez = mutez =>
  Math.round((parseInt(mutez) / 1000000 + Number.EPSILON) * 100) / 100;

const App = () => {
  const [contractInstance, setContractInstance] = useState(undefined);
  const [coffeeMenu, setCoffeeMenu] = useState([]);
  const [userAddress, setUserAddress] = useState(undefined);
  const [balance, setBalance] = useState(undefined);
  const [isOwner, setIsOwner] = useState(false);
  const [contractBalance, setContractBalance] = useState(0);
  const tezbridge = window.tezbridge;

  const initWallet = async () => {
    try {
      // sets rpc host
      const rpc = await tezbridge.request({
        method: "set_host",
        host: "http://localhost:8732"
      });
      // gets user's address
      const _address = await tezbridge.request({ method: "get_source" });
      setUserAddress(_address);
      // gets user's balance
      const _balance = await Tezos.tz.getBalance(_address);
      setBalance(_balance);
      // compares user's address with owner's address
      if (storage.owner === _address) {
        setIsOwner(true);
        const _contractBalance = await Tezos.tz.getBalance(KT_token);
        setContractBalance(_contractBalance.c[0]);
      }
    } catch (error) {
      console.log("error fetching the address or balance:", error);
    }
  };

  const mint = async () => {

    //放一下要mint的金额的对话框
    // sends mint request
    const op = await contractInstance.methods.mint([["unit"]]).send();
    // waits for confirmation
    await op.confirmation(30);
    // if confirmed
    if (op.includedInBlock !== Infinity) {
      const newBalance = await Tezos.tz.getBalance(userAddress);
      setBalance(newBalance);
    } else {
      console.log("error");
    }
  };

  useEffect(() => {
    (async () => {
      // sets RPC
      Tezos.setProvider({
        rpc: "http://localhost:8732",
        signer: new TezBridgeSigner()
      });
      // fetches contract storage
      const tokenContract = await Tezos.contract.at(KT_token);
      setTokenInstance(tokenContract);
      const storage = await tokenContract.storage();
      // creates coffee menu
      let coffees = [];
      for (let key of storage.menu.keys()) {
        coffees.push({ name: key, price: storage.menu.get(key).c[0] });
      }
      // updates state
      setCoffeeMenu(coffees);
    })();
  }, []);

  return (
    <div className="App">
      <div className="wallet">
        {balance === undefined ? (
          <button
            className="button is-info is-light is-small"
            onClick={initWallet}
          >
            Connect your wallet
          </button>
        ) : (
          <>
            <span className="balance">ꜩ {balance.toNumber() / 1000000}</span>
            <div className="field is-grouped">
              <p className="control">
                <button
                  className="button is-success is-light is-small"
                  onClick={async () => {
                    setUserAddress(undefined);
                    setBalance(undefined);
                    setIsOwner(undefined);
                    await initWallet();
                  }}
                >
                  {shortenAddress(userAddress)}
                </button>
              </p>
              {isOwner && (
                <p className="control">
                  <button
                    className="button is-warning is-light is-small"
                    onClick={mint}
                  >
                    Mint ꜩ {mutezToTez(contractBalance)}
                  </button>
                </p>
              )}
            </div>
          </>
        )}
      </div>
      <div className="app-title">Café Tezos</div>
      <div className="logo">
        <img src="coffee-maker.png" alt="logo" />
      </div>
      {coffeeMenu.length === 0 ? (
        "Loading the menu..."
      ) : (
        <Menu
          coffeeMenu={coffeeMenu}
          ledgerInstance={tokenInstance}
          userAddress={userAddress}
          setBalance={setBalance}
          Tezos={Tezos}
        />
      )}
      {/*<div>Icons made by <a href="https://www.flaticon.com/authors/iconixar" title="iconixar">iconixar</a> from <a href="https://www.flaticon.com/" title="Flaticon">www.flaticon.com</a><a href="https://www.vecteezy.com/free-vector/coffee-cute">Coffee Cute Vectors by Vecteezy</a></div>*/}
    </div>
  );
};

export default App;
